import React, {Component} from 'react';
import ZipItem from './ZipItem';
import ZipColumn from './ZipColumn';
import ZipRow from './ZipRow';
import './Display.css';

class Display extends Component {
    constructor(props){
	super(props);
	this.state = {
	    rend: {
		   'zip':this.renderZip,
		   'city':this.renderCity
		  }
	};
    }

    renderZip = () => {
	var items = this.props.arr.map(function(item, i) {
	    return (

		    <ZipItem loc={item[0]} long={item[1]} lat={item[2]} pop={item[3]} index={i}/>
	    );});
	return items;
    }

    renderCity = () => {
	let hold = [];
	let length = this.props.arr.length;
	let columns = Math.ceil(length / 40);
	let prepend = ["<ZipColumn count=columns />"];
	var items = this.props.arr.map(function (item, i) {   
	    let itemsInner = [];
	    while (i < length) {
		if(i%40 === 0 || i === length-1) {
		    itemsInner.push("<ZipRow arr=hold />");
		    hold = [];
		    break;
		}
		itemsInner.push(hold);
	    }

	    return itemsInner;
	});

	return prepend.concat(items);	
    }

    render() {
	let items = this.state.rend[this.props.field]();
	console.log("The field: ", this.props.field);
	console.log("Items: ", items);
	return (
	    <div className="DISPLAY">
	      {items}
	    </div>
	);
    };
}

export default Display;
